<div class="container-fluid pt-2" style="min-height: 600px;">
  <h1 class="text-center">My Tasks</h1>

<main>
  <!--error and success messages-->
  <div class="row justify-content-center pt-4">
    <div class="col-sm-10 col-md-8 col-lg-6">

      <div *ngIf="errorMessage">
        <div class="alert alert-danger" role="alert">
          {{ errorMessage  }}
        </div>
      </div>

      <div *ngIf="successMessage">
        <div class="alert alert-success" role="alert">
          {{ successMessage }}
        </div>
      </div>
    </div>
  </div>

  <!--new task input field-->
<div class="container-task">
<div class="row justify-content-center pt-4">
  <div class="col-sm-10 col-md-8 col-lg-6">
    <form [formGroup]="newTaskForm" (ngSubmit)="addTask();">
      <div class="input-group mb-3">
        <input type="text" class="form-control" formControlName="text" placeholder="Add new task">
        <button class="btn btn-outline-secondary" type="submit" [disabled]="!newTaskForm.valid" id="btnAdd">Add</button>
      </div>

      <!--validation required field-->
      <div *ngIf="newTaskForm.controls['text'].touched && newTaskForm.controls['text'].hasError('required')">
        <div class="alert alert-danger alert-dismissible fade show" role="alert">
          <strong>Please enter a task!</strong>
          <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>
        </div>
      </div>

      <!--validation min field -->
      <div *ngIf="newTaskForm.controls['text'].touched && newTaskForm.controls['text'].hasError('minlength')">
        <div class="alert alert-danger alert-dismissible fade show" role="alert">
          <strong>The task must be at least 3 characters long!</strong>
          <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>
        </div>
      </div>

      <!--validation max field-->
      <div *ngIf="newTaskForm.controls['text'].touched && newTaskForm.controls['text'].hasError('maxlength')">
        <div class="alert alert-danger alert-dismissible fade show" role="alert">
          <strong>The field must be at most 50 characters long!</strong>
          <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>
        </div>
      </div>
    </form>

  </div>
</div>


  <!-- to do and done container-->
  <div class="row gy-2 justify-content-center">

    <!--to do column-->
    <div class="col-sm-5 col-md-5 col-lg-3">
      <h4 class="text-center">ToDo</h4>

      <div class="p-3 border bg-light">

        <div class="container">

          <div *ngIf="todo.length === 0">
            <div class="alert alert-info">
              No tasks to do yet!
            </div>
          </div>

          <div *ngFor="let item of todo" class="mt-2">
            <div class="row border bg-white">

              <!--column 1 of task item-->
              <div class="col-sm-6">
                <div class="card-body">
                  {{ item.text }}
                </div>
              </div>
              <!-- end of column 1-->

              <!--column 2 of task item-->
              <div class="col-sm-6">
                <div class="card-body float-end">
                  <button class="btn">
                    <span class="material-icons">delete</span>
                  </button>
                </div>
              </div>
              <!--end column 2-->
            </div>
          </div>

        </div>
      </div>
    </div>
    <!--end todo column-->

    <!--done column-->
    <div class="col-sm-5 col-md-5 col-lg-3">
      <h4 class="text-center">Done</h4>

      <div class="p-3 border bg-light">

        <div class="container">

          <div *ngIf="done.length === 0">
            <div class="alert alert-info">
              No tasks done yet!
            </div>
          </div>

          <div *ngFor="let item of done" class="mt-2">
            <div class="row border bg-white">

              <!--column 1 of task item-->
              <div class="col-sm-6">
                <div class="card-body">
                  {{ item.text }}
                </div>
              </div>
              <!-- end of column 1-->

              <!--column 2 of task item-->
              <div class="col-sm-6">
                <div class="card-body float-end">
                  <button class="btn">
                    <span class="material-icons">delete</span>
                  </button>
                </div>
              </div>
              <!--end column 2-->
            </div>
          </div>

        </div>
      </div>
    </div>
    <!--end done column-->
  </div>
</div>
</main>
</div>
