import { Component } from '@angular/core';

@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.css']
})
export class ContactComponent {

}


<div class="container-contact">

  <h1>Contact Us In The Following Ways!</h1>



  <!--image display-->
  <div>
    <img class="contact-pic" src="./assets/Ravenclaw-Banner.jpeg" alt="picture of ravenclaw banner" width="50%" />
    <!--photo attribution and link-->
    <p class="attribution-link">Source: <a href="#">name goes here</a></p>
  </div>

</div>
